import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import _ from "lodash";
import s from "./Rent.module.css";
import Btn from "../Button/Btn";
import "./popup.css";

const Rent = (props) => {
    const [result, setResult] = useState({
        message: "",
        success: false,
    });

    const { register, errors, handleSubmit } = useForm();

    const validators = {
        required: "Введено недопустимое значение или поле не заполнено",
    };

    async function onSubmit(values) {
        console.log(values);

        const response = await fetch("http://localhost:5000/server", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(values),
        });

        const result = await response.json();

        // обновляем состояние
        setResult({
            message: result,
            success: response.ok,
        });
    }

    return (
        <div className={s.cont__rend} id="contacts">
            <h2 className={s.title}>Заказать оборудование</h2>
            <form className={s.form} onSubmit={handleSubmit(onSubmit)}>
                <div className={s.form__input}>
                    <label htmlFor="phone" className={s.name}>
                        Телефон
                    </label>
                    {result && (
                        <div className="popup">
                            <p className="status">{validators.required}</p>
                        </div>
                    )}
                    <div className={s.valid__cont}>
                        <input
                            className="inp"
                            type="tel"
                            name=""
                            id="phone"
                            placeholder="+ 7 (999)-999-99-99"
                        />
                        <svg
                            version="1.0"
                            xmlns="http://www.w3.org/2000/svg"
                            width="25px"
                            height="25px"
                            viewBox="0 0 1280.000000 1034.000000"
                            preserveAspectRatio="xMidYMid meet"
                        >
                            <g
                                transform="translate(0.000000,1034.000000) scale(0.100000,-0.100000)"
                                fill="#000000"
                                stroke="none"
                            >
                                <path
                                    d="M7625 7270 l-3070 -3070 -1232 1232 -1233 1233 -1045 -1045 c-691
                                    -691 -1045 -1052 -1045 -1065 0 -29 4527 -4555 4557 -4555 15 0 1188 1169
                                    4130 4115 2259 2263 4110 4122 4112 4131 4 20 -2065 2094 -2089 2094 -8 0
                                    -1397 -1381 -3085 -3070z"
                                />
                            </g>
                        </svg>
                    </div>
                </div>
                <div className={s.form__input}>
                    <label htmlFor="name" className={s.name}>
                        Имя
                    </label>
                    {result && (
                        <div className="popup">
                            <p className="status">{validators.required}</p>
                        </div>
                    )}
                    <div className={s.valid__cont}>
                        <input type="text" name="" id="name" className="inp" />
                        {!result && <svg
                            version="1.0"
                            xmlns="http://www.w3.org/2000/svg"
                            width="25px"
                            height="25px"
                            viewBox="0 0 1280.000000 1034.000000"
                            preserveAspectRatio="xMidYMid meet"
                        >
                            <g
                                transform="translate(0.000000,1034.000000) scale(0.100000,-0.100000)"
                                fill="#000000"
                                stroke="none"
                            >
                                <path
                                    d="M7625 7270 l-3070 -3070 -1232 1232 -1233 1233 -1045 -1045 c-691
                                    -691 -1045 -1052 -1045 -1065 0 -29 4527 -4555 4557 -4555 15 0 1188 1169
                                    4130 4115 2259 2263 4110 4122 4112 4131 4 20 -2065 2094 -2089 2094 -8 0
                                    -1397 -1381 -3085 -3070z"
                                />
                            </g>
                        </svg>}
                    </div>
                </div>
                <div className={s.rend}>
                    <button className={s.arend} type="submit">
                        Заказать
                    </button>
                    <p className={s.politic}>
                        Нажимая на кнопку, я соглашаюсь c
                        <span> Политикой конфиденциальности</span>
                    </p>
                </div>
            </form>
        </div>
    );
};

export default Rent;
