import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import _ from "lodash";
import s from "./Rent.module.css";
import Btn from "../Button/Btn";
import "./popup.css";

const Rent = (props) => {
    const [result, setResult] = useState({
        message: "",
        success: false,
    });





    return (
        <div className={s.cont__rend} id="contacts">
            <h2 className={s.title}>Заказать оборудование</h2>
            <form className={s.form} onSubmit={handleSubmit(onSubmit)}>
                <div className={s.form__input}>
                    <label htmlFor="phone" className={s.name}>
                        Телефон
                    </label>
                    {result && (
                        <div className="popup">
                            <p className="status"></p>
                        </div>
                    )}
                    <div className={s.valid__cont}>
                        <input
                            className="inp"
                            type="tel"
                            name=""
                            id="phone"
                            placeholder="+ 7 (999)-999-99-99"
                        />
                    </div>
                </div>
                <div className={s.form__input}>
                    <label htmlFor="name" className={s.name}>
                        Имя
                    </label>
                    {result && (
                        <div className="popup">
                            <p className="status"></p>
                        </div>
                    )}
                    <div className={s.valid__cont}>
                        <input
                            type="text"
                            name=""
                            id="name"
                            className="inp"
                            ref={register({
                                ...validators,
                                minLength: {
                                    value: 2,
                                    message: "Не менее двух букв",
                                },
                                maxLength: {
                                    value: 10,
                                    message: "Не более десяти букв",
                                },
                                pattern: {
                                    value: /[А-ЯЁ]{2,10}/i,
                                    message: "Только киррилица",
                                },
                            })}
                        />
                    </div>
                </div>
                <div className={s.rend}>
                    <button className={s.arend} type="submit">Заказать</button>
                    <p className={s.politic}>
                        Нажимая на кнопку, я соглашаюсь c
                        <span> Политикой конфиденциальности</span>
                    </p>
                </div>
            </form>
        </div>
    );
};

export default Rent;
