                    {nameDirty && nameError && (
                        <div className="popup">
                            <p className="status">{nameError}</p>
                        </div>
                    )}
                    <div className={s.valid__cont}>
                        <input
                            onChange={(e) => nameHandler(e)}
                            type="text"
