        $('.popup').show("100");
        $('.status').text('<p>Ваша заявка принята</p>');
    };

    let closePopUp = () => {
        $(document).mouseup(e => {
            if (!$('.popup').is(e.target) && $('.popup').has(e.target).length === 0) {
                $('.popup').hide("100");
            }
        });
    }

    $(document).ready(() => {
        $('.popup').hide();
        $("#phone").mask("+ 7 (999)-999-99-99");

        closePopUp();

