import React from "react";
import mask from "jquery-mask-plugin";
import 'jquery-validation';
import $, { inArray } from "jquery";
import s from "./Rent.module.css";
import Btn from "../Button/Btn";
import "./popup.css";

const Rent = () => {
    $(document).ready(() => {
        $(".valid").hide();
        $(".popup").hide();
        $("#phone").mask("+ 7 (999)-999-99-99");
        const validation = () => {
            let regex = /^(\+7|7|8)?[\s\-]?\(?[489][0-9]{2}\)?[\s\-]?[0-9]{3}[\s\-]?[0-9]{2}[\s\-]?[0-9]{2}$/;
            let phoneNum = $("#phone").val();

            // if (
            //     ($("#name").val() === "" ||
            //     ($("#name").val() === " " ) && (phoneNum !== ""))
            // ) {
            //     $("#name").parent().parent().find(".popup").show("200");
            //     $("#name")
            //         .parent()
            //         .parent()
            //         .find(".status")
            //         .text("Введено недопустимое значения или поле не заполнено");
            // } else if (
            //     (phoneNum === "") &&
            //     ($("#name").val() !== "" || $("#name").val() !== " ")
            // ) {
            //     $("#phone").parent().parent().find(".popup").show("200");
            //     $("#phone")
            //         .parent()
            //         .parent()
            //         .find(".status")
            //         .text("Введено недопустимое значения или поле не заполнено");
            // } else if (
            //     ($("#name").val() === "" ||
            //     $("#name").val() === " ") &&
            //     ($("#phone").val() === "")
            // ) {
            //     $(".popup").show("200");
            //     $(".status").text(
            //         "Введено недопустимое значения или поле не заполнено"
            //     );
            // } else {
            //     popUpValid();
            // }

            $('#form').validate({
                rules: {
                    tel: {
                        required: true,
                        tel: true
                    },

                    name: {
                        required: true
                    }
                },

                messages: {
                    tel: {
                        required: popUpUnvalid()
                    },

                    name: {
                        required: popUpUnvalid()
                    }
                },
                submitHandler: popUpValid()
            });

        };
    });

    const popUpValid = () => {
        $(".valid").show("200");
        setTimeout(() => {
            $(".valid").hide("100");
            $("input").val("");
        }, 1500);
    };

    const popUpUnvalid = () => {
        $(".popup").show("200");
        $(".status").text("Введено недопустимое значения или поле не заполнено");

        $(document).mouseup((e) => {
            if (
                !$(".popup").is(e.target) &&
                $(".popup").has(e.target).length === 0
            ) {
                $(".popup").hide("100");
            }
        });
    }

    // validation for form



    return (
        <div className={s.cont__rend}>
            <h2 className={s.title}>Заказать оборудование</h2>
            <form className={s.form} id="form" role="form">
                <div className={s.form__input}>
                    <label htmlFor="phone" className={s.name}>
                        Телефон
                    </label>
                    <div className="popup">
                        <p className="status"></p>
                    </div>
                    <div className={s.valid__cont}>
                        <input
                            className="inp"
                            type="tel"
                            name=""
                            id="phone"
                            placeholder="+ 7 (999)-999-99-99"
                        />
                        <svg
                            version="1.0"
                            xmlns="http://www.w3.org/2000/svg"
                            width="15px"
                            height="15px"
                            viewBox="0 0 1280.000000 1034.000000"
                            preserveAspectRatio="xMidYMid meet"
                            className="valid"
                        >
                            <metadata>
                                Created by potrace 1.15, written by Peter
                                Selinger 2001-2017
                            </metadata>
                            <g
                                transform="translate(0.000000,1034.000000) scale(0.100000,-0.100000)"
                                fill="#000000"
                                stroke="none"
                            >
                                <path
                                    d="M7625 7270 l-3070 -3070 -1232 1232 -1233 1233 -1045 -1045 c-691
                                    -691 -1045 -1052 -1045 -1065 0 -29 4527 -4555 4557 -4555 15 0 1188 1169
                                    4130 4115 2259 2263 4110 4122 4112 4131 4 20 -2065 2094 -2089 2094 -8 0
                                    -1397 -1381 -3085 -3070z"
                                />
                            </g>
                        </svg>
                    </div>
                </div>
                <div className={s.form__input}>
                    <label htmlFor="name" className={s.name}>
                        Имя
                    </label>
                    <div className="popup">
                        <p className="status"></p>
                    </div>
                    <div className={s.valid__cont}>
                        <input type="text" name="" id="name" className="inp" />
                        <svg
                            version="1.0"
                            xmlns="http://www.w3.org/2000/svg"
                            width="15px"
                            height="15px"
                            viewBox="0 0 1280.000000 1034.000000"
                            preserveAspectRatio="xMidYMid meet"
                            className="valid"
                        >
                            <metadata>
                                Created by potrace 1.15, written by Peter
                                Selinger 2001-2017
                            </metadata>
                            <g
                                transform="translate(0.000000,1034.000000) scale(0.100000,-0.100000)"
                                fill="#000000"
                                stroke="none"
                            >
                                <path
                                    d="M7625 7270 l-3070 -3070 -1232 1232 -1233 1233 -1045 -1045 c-691
                                    -691 -1045 -1052 -1045 -1065 0 -29 4527 -4555 4557 -4555 15 0 1188 1169
                                    4130 4115 2259 2263 4110 4122 4112 4131 4 20 -2065 2094 -2089 2094 -8 0
                                    -1397 -1381 -3085 -3070z"
                                />
                            </g>
                        </svg>
                    </div>
                </div>
                <div className={s.rend}>
                    <button className={s.arend} type="submit" onClick={validation}>
                        Заказать
                    </button>
                    <p className={s.politic}>
                        Нажимая на кнопку, я соглашаюсь c
                        <span> Политикой конфиденциальности</span>
                    </p>
                </div>
            </form>
        </div>
    );
};

export default Rent;
