import React from "react";
import s from "./Btn.module.css";
import $ from 'jquery';

const Btn = () => {
    const Scroll = () => {
        $('#btn_click').on('click', function(e){
            e.preventDefault();
            $('html, body').animate({scrollTop: $('#contacts').offset().top}, 2000);
        });
    };

    return <button className={s.arend} onClick={ Scroll } id="btn_click">Заказать</button>;
};

export default Btn;
